INSERT INTO `detalle` VALUES ('5', '2', '4', '9', '* De ser el caso, plano de sostenimiento de excavaciones de acuerdo con lo establecido en el artículo 33 de la Norma E 050 del RNE acompañado de la memoria descriptiva que precise las características de la obra, además de las edificaciones colindantes indicando el número de pisos y sótanos, complementando con fotos.');
