INSERT INTO `detalle` VALUES ('5', '3', '1', '33', '* Todos los planos y documentos técnicos deben estar sellados y firmados por el profesional responsable de los mismos y asi como por el  propietario o solicitante.');
INSERT INTO `detalle` VALUES ('5', '3', '1', '34', '* El pago por revisión del proyecto de edificación, será depositado por los administrados en las cuentas que señalen cada uno de los respectivos Colegios Profesionales o instituciones representadas.');
INSERT INTO `detalle` VALUES ('5', '3', '1', '35', '* En caso de proyectos de gran magnitud, los planos podrán ser presentados en secciones con escala conveniente que permita su fácil lectura, conjuntamente con el plano del proyecto integral.');
INSERT INTO `detalle` VALUES ('5', '3', '1', '36', '* Para los casos de edificación se debe acreditar que el predio cuente, por lo menos, con el correspondiente Proyecto  de Habilitación Urbana aprobado.');
